mapper.select:
SELECT h.`ID`,`TITLE`,PRICE,PUBDATE,CONTACT,FLOORAGE,path,`DESCRIPTION`, t.`NAME` AS tname,s.`NAME` AS sname,d.`NAME` AS dname FROM house AS h INNER JOIN TYPE AS t ON h.`TYPE_ID`=t.`ID` INNER JOIN street AS s ON h.`STREET_ID`=s.`ID` INNER JOIN district AS d ON d.`ID`=s.`DISTRICT_ID`
and floorage=#{floorage}
and price=#{price}
and ispass=#{ispass} and isdel=0

SELECT h.`ID`,`TITLE`,PRICE,PUBDATE,CONTACT,FLOORAGE,path,`DESCRIPTION`,
t.`NAME` AS tname,s.`NAME` AS sname,d.`NAME` AS dname
FROM house AS h
INNER JOIN TYPE AS t ON h.`TYPE_ID`=t.`ID`
INNER JOIN street AS s ON h.`STREET_ID`=s.`ID`
INNER JOIN district AS d ON d.`ID`=s.`DISTRICT_ID`
where ispass=1 and isdel=0

    and title like concat('%',#{title},'%')


    and h.type_Id = #{typeId}


  and d.Id = #{did}


  and h.street_Id = #{streetId}


  and price >= #{startprcie}


  and price <= #{endprcie}
